+ (UIBezierPath *)complexShape2
{
    UIBezierPath *shapePath2 = [UIBezierPath complexShape1];
    [shapePath2 applyTransform:CGAffineTransformMakeTranslation(-CGRectGetMaxX([shapePath2 bounds]), -CGRectGetMaxY([shapePath2 bounds]))];
    [shapePath2 applyTransform:CGAffineTransformMakeScale(-1.0, -1.0)];

    [shapePath2 applyTransform:CGAffineTransformMakeTranslation(50, 90)];

    return shapePath2;
}

